import "./UserInfo.css";
import UserIcon from "../main/img/user_icon.png";
import { fetch_data } from "../../utils/api";
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

export const UpdateUser = (props: any) => {
    console.log(props)
    const navigate = useNavigate();
    const [username, setUsername] = useState(props.Username || "");
    const [name, setName] = useState(props.Name || "");
    const [surname, setSurname] = useState(props.Surname || "");
    const [email, setEmail] = useState(props.Email || "");
    const [phone, setPhone] = useState(props.Phone || "");
    const [driverLicence, setDriverLicence] = useState(props.DriverLicence || "");


    const handleUsernameChange = (event: any) => {
      setUsername(event.target.value);
    };

    const handleNameChange = (event: any) => {
      setName(event.target.value);
    };

    const handleSurnameChange = (event: any) => {
      setSurname(event.target.value);
    };

    const handleEmailChange = (event: any) => {
      setEmail(event.target.value);
    };

    const handlePhoneChange = (event: any) => {
      setPhone(event.target.value);
    };

    const handleDriverLicenceChange = (event: any) => {
      setDriverLicence(event.target.value);
    };


    const One = async () => {
      const data = {
        username : username,
        first_name: name,
        last_name: surname,
        Email: email,
        Phone: phone,
        DriverLicence: driverLicence,
      };
      await fetch_data("/user/updateMe", "PUT", data);
      console.log(localStorage.getItem('access_token'))
      navigate('/user');
    };

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        if (newPassword !== confirmNewPassword) {
          window.alert("Passwords do not match");
          return;
        }
        try {
          const response = await fetch_data("/user/", "PUT", {
            name: firstName,
            surname: lastName,
            email: email,
            old_password: oldPassword,
            password: newPassword,
          });
          window.alert("Account updated successfully");
        } catch (error: any) {
          window.alert(error);
        }
      };

    return (
      <div className="container">
        <h2 className="text-center my-4">Your Page</h2>

        <div className="col-md-8 user-wrapper mx-auto py-4 shadow-lg p-3 mb-5 bg-white">
          <div className="d-flex justify-content-center align-items-center">
            <img src={UserIcon} alt="user" width="200" height="190" />
          </div>

          <div className="user-info-wrapper mx-2">

            <div className="user-info-row my-2 mx-1">
              <div className="user-info-label">
                <strong>Username:</strong>
              </div>
              <div className="user-info-value">
                <input
                  type="text"
                  value={username}
                  onChange={handleUsernameChange}
                  className="form-control"
                />
              </div>
            </div>

            <div className="user-info-row my-2 mx-1">
              <div className="user-info-label">
                <strong>Name:</strong>
              </div>
              <div className="user-info-value">
                <input
                  type="text"
                  value={name}
                  onChange={handleNameChange}
                  className="form-control"
                />
              </div>
            </div>

            <div className="user-info-row my-2 mx-1">
              <div className="user-info-label">
                <strong>Surname:</strong>
              </div>
              <div className="user-info-value">
                <input
                  type="text"
                  value={surname}
                  onChange={handleSurnameChange}
                  className="form-control"
                />
              </div>
            </div>

            <div className="user-info-row my-2 mx-1">
              <div className="user-info-label">
                <strong>Email:</strong>
              </div>
              <div className="user-info-value">
                <input
                  type="text"
                  value={email}
                  onChange={handleEmailChange}
                  className="form-control"
                />
              </div>
            </div>

            <div className="user-info-row my-2 mx-1">
              <div className="user-info-label">
                <strong>Phone:</strong>
              </div>
              <div className="user-info-value">
                <input
                  type="text"
                  value={phone}
                  onChange={handlePhoneChange}
                  className="form-control"
                />
              </div>
            </div>

            <div className="user-info-row my-2 mx-1">
              <div className="user-info-label">
                <strong>Driver Licence:</strong>
              </div>
              <div className="user-info-value">
                <input
                  type="text"
                  value={driverLicence}
                  onChange={handleDriverLicenceChange}
                  className="form-control"
                />
              </div>
            </div>

            <button className="edit-user-button my-2 mx-1" onClick={handleSaveChanges}>
                Save Changes
            </button>

          </div>
          </div>
          </div>
    )};
