import React, { useEffect } from "react";
import UserInfo from './UserInfo'
import { fetch_data } from "../../utils/api";

const User = () => {
  const [username, setUsername] = React.useState("");
  const [first_name, setFirstName] = React.useState("");
  const [last_name, setLastName] = React.useState("");
  const [email, setEmail] = React.useState("");
  const [phone, setPhone] = React.useState("");
  const [drive_license, setDrive_license] = React.useState("");



  const fetchData = async () => {
    fetch('http://127.0.0.1:63341/user/me', {
            method: "GET",
            headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("access_token")}`
            }
        })
        .then(response => response.json())
        .then(data => {
            // Update the HTML elements with user data
            username = data.username;
            name.textContent = data.first_name;
            surname.textContent = data.last_name;
            email.textContent = data.email;
            phone.textContent = data.phone;
            license.textContent = data.drive_license;
          })
          .catch(error => console.error(error));



    const data = await fetch_data("/user/me", "GET");
    setUsername(data.username);
    setFirstName(data.first_name);
    setLastName(data.last_name);
    setEmail(data.email);
    setPhone(data.phone);
    setDrive_license(data.drive_license);
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div>
      <UserInfo
        username={username}
        Name={first_name}
        Surname={last_name}
        Email={email}
        Phone={phone}
        DriverLicence={drive_license}
      />
    </div>
  );
};

export default User;
