import React, { useState, useEffect } from 'react';
import './CarsButtons.css';

export const AvailButton = () => {
  const [cars, setCars] = useState([]);
  const [status, setStatus] = useState('available');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:63341/car/getByStatus${status.charAt(0).toUpperCase()}${status.slice(1)}`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${localStorage.getItem('access_token')}`
          }
        });

        if (!response.ok) {
          throw new Error(response.status);
        }

        const data = await response.json();
        setCars(data);
      } catch (error) {
        console.error(`Error fetching data: ${error}`);
      }
    };

    fetchData();
  }, [status]);

  const handleStatusClick = (newStatus) => {
    setStatus(newStatus);
  };

  return (
    <div>
      <div className="my-4 d-flex justify-content-center">
        <button
          className={`car-status-button mx-2 ${status === 'available' ? 'selected' : ''}`}
          onClick={() => handleStatusClick('available')}
        >
          available
        </button>
        <button
          className={`car-status-button mx-2 ${status === 'unavailable' ? 'selected' : ''}`}
          onClick={() => handleStatusClick('unavailable')}
        >
          unavailable
        </button>
      </div>
      <div className="car-list">
        {cars.map((car) => (
          <div className="col-md-4 col-sm-6 col-12" key={car.car_id}>
            <a href={`rental.html?car_id=${car.car_id}`}>
              <div className="car-image-wrapper">
                <img src={car.image_path} />
                <p className="car-name text-lg">{car.mark}</p>
                <p className="car-price">{`${car.price} $/day`}</p>
              </div>
            </a>
          </div>
        ))}
      </div>
    </div>
  );
};
