import { useState, useEffect } from 'react'
import "./Cars.css"
import CarInstance from './CarInstance'
import { fetch_data } from "../../utils/api";
import Car1 from "./img/image1.jpeg"
import Car2 from "./img/image2.jpeg"
import Car3 from "./img/image3.webp"
import Car4 from "./img/image4.jpeg"
import Car5 from "./img/image5.jpeg"
import Car6 from "./img/image6.jpeg"
import Car7 from "./img/image7.jpeg"
import Car8 from "./img/image8.jpeg"
import Car9 from "./img/image9.webp"

interface Car {
    car_id: number;
    mark: string;
    category: string;
    price: number;
    transmission: string;
    status: string;
    image_path: any;
  }

export const Cars = () => {
    const [cars, setCars] = useState<Car[]>([]);

    useEffect(() => {
      getCars();
    }, []);

    const getCars = async () => {
      try {
        const response = await fetch('http://127.0.0.1:63341/car/getAll', {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("access_token")}`
          }
        });
        const data = await response.json();
        console.log(data)
        setCars(data);
      } catch (error) {
        console.error(error);
      }
    };

    const getCarsByStatusAvail = async () => {
        try {
          const response = await fetch('http://127.0.0.1:63341/car/getByStatusAvail', {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
              Authorization: `Bearer ${localStorage.getItem('access_token')}`,
            },
          });
          const data = await response.json();
          console.log(data);
          clearCars();
          setCars(data);
          setShowCars(true);

        } catch (error) {
          console.error(error);
        }
      };

      const getCarsByStatusUnavail = async () => {
        try {
          const response = await fetch('http://127.0.0.1:63341/car/getByStatusUnavail', {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
              Authorization: `Bearer ${localStorage.getItem('access_token')}`,
            },
          });
          const data = await response.json();
          console.log(data);
          clearCars();
          setCars(data);
          setShowCars(true);
        } catch (error) {
          console.error(error);
        }
      };

      const clearCars = () => {
        setCars([]);
      };

    return (
        <>
            <div className="my-4 d-flex justify-content-center">
                <button className="car-status-button mx-2" onClick={() => getCarsByStatusAvail()}>
                    available
                </button>
                <button className="car-status-button mx-2" onClick={() => getCarsByStatusUnavail()}>
                    unavailable
                </button>
            </div>

            <div className="car-container my-4">
                <div className="row row-cols-3 gy-4 car-list" id="car-list">
                    {cars.map((car) => (
                        <CarInstance
                            key={car.car_id}
                            strMark={car.mark}
                            strPrice={car.price.toString()}
                            strImagePath={Car8}
                        />
                    ))}
                </div>
            </div>
        </>
    );
}
