import React, { useState, useEffect } from 'react';
import './CarsButtons.css';
import CarInstance from './CarInstance';
import Car8 from './img/image8.jpeg';

interface Car {
  car_id: number;
  mark: string;
  category: string;
  price: number;
  transmission: string;
  status: string;
  image_path: any;
}

type Cars = Car[];

export const AvailButton = () => {
  const [cars, setCars] = useState<Cars>([]);
  const [showCars, setShowCars] = useState<boolean>(true);

  const getCarsByStatusAvail = async () => {
    try {
      const response = await fetch('http://127.0.0.1:63341/car/getByStatusAvail', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('access_token')}`,
        },
      });
      const data = await response.json();
      console.log(data);
      clearCars();
      setCars(data);
      setShowCars(true);

    } catch (error) {
      console.error(error);
    }
  };

  const getCarsByStatusUnavail = async () => {
    try {
      const response = await fetch('http://127.0.0.1:63341/car/getByStatusUnavail', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('access_token')}`,
        },
      });
      const data = await response.json();
      console.log(data);
      clearCars();
      setCars(data);
      setShowCars(true);
    } catch (error) {
      console.error(error);
    }
  };

  const clearCars = () => {
    setCars([]);
  };

  return (
    <>
    <div className="my-4 d-flex justify-content-center">
      <button className="car-status-button mx-2" onClick={() => getCarsByStatusAvail()}>
        available
      </button>
      <button className="car-status-button mx-2" onClick={() => getCarsByStatusUnavail()}>
        unavailable
      </button>
    </div>


      {/* Render the cars */}
      {showCars &&
        cars.map((car) => (
          <CarInstance key={car.car_id} strMark={car.mark} strPrice={car.price.toString()} strImagePath={Car8} />
        ))}
    </>
  );
};
